				function createToken() {
				console.log("create token");
				fetch("/token/v1/token", {
				method: "POST"
				}).then(res => {
				if (res.status == 200) {
				window.location.reload();
				}
				else {
				throw Error(res.statusText);
				}
				}).catch(error => {
				console.log("Problem in sending token: ", error);
				closeModal();
				});
				}
				function init() {
				fetch("/token/v1/token/status", {
				method: "GET"
				}).then(res => {
				if (res.status == 200) {
				return res.json();
				}
				else {
				throw Error(res.statusText);
				}
				}).then(result => {
				if (Object.keys(result).length != 0) {
				tokenWidget.style.display = 'flex';
				var tokenSectionData = result.section_data;
				var tokenPopupData = result.popup_data;
				var tokenMetaData = result.meta_data;
				if(tokenMetaData) {
				var memberType = tokenMetaData.member_type;
				var deliveryDate = tokenMetaData.est_delivery_date;
				if(memberType == "normal") {
				memberType = "";
				}
				}
				tokenWidgetHeaderTitle.innerHTML = tokenSectionData.title;
				tokenWidgetTooltipText.innerHTML = tokenSectionData.hint;
				tokenWidgetDesc.innerHTML = tokenSectionData.desc;
				tokenWidgetActionInfo.innerHTML = tokenSectionData.sub_desc;
				tokenWidgetBtnText.innerHTML = tokenSectionData.btn_text;
				tokenWidgetSubDesc.innerHTML = tokenSectionData.note;
				tokenWidgetBtnStatus = tokenSectionData.btn_status;
				tokenStatusData = tokenSectionData.token_status;
				if(!tokenSectionData.hint) {
				tokenWidgetTooltip.style.display = "none";
				}
				switch (tokenStatusData) {
				case "Issued":
				tokenWidgetActionInfo.style.display = "block";
				tokenWidgetDesc.style.marginBottom = "10px";
				tokenSectionData.title = tokenSectionData.title.replace("meta_data.member_type", memberType);
				tokenSectionData.title = tokenSectionData.title.replace("{{", "");
				tokenSectionData.title = tokenSectionData.title.replace("}}", "");
				tokenWidgetHeaderTitle.innerHTML = tokenSectionData.title;
				tokenSectionData.desc = tokenSectionData.desc.replace("meta_data.est_delivery_date", deliveryDate);
				tokenSectionData.desc = tokenSectionData.desc.replace("{{", "");
				tokenSectionData.desc = tokenSectionData.desc.replace("}}", "");
				tokenWidgetDesc.innerHTML = tokenSectionData.desc;
				break;
				case "Active":
				tokenWidgetDesc.style.color = "#e95f62";
				tokenWidgetBtn.style.display = "none";
				tokenWidgetActions.style.flexDirection = "unset";
				tokenWidgetHeaderTitle.style.marginTop = "20px";
				tokenWidgetActions.style.justifyContent = "unset";
				break;
				default:
				tokenWidgetActionInfo.style.display = "none";
				tokenWidgetDesc.style.marginBottom = "15px";
				tokenWidgetDesc.style.color = "#3b4556";
				tokenWidgetBtn.style.display = "block";
				tokenWidgetTooltip.style.display = "block";
				tokenWidgetHeaderTitle.style.marginTop = "1px";
				tokenWidgetActions.style.flexDirection = "row-reverse";
				tokenWidgetActions.style.justifyContent = "space-between";
				break;
				}
				if (tokenWidgetBtnStatus) {
				tokenWidgetBtn.addEventListener('click', function () {
				backdrop.style.display = 'block';
				tokenWidgetModal.style.display = 'flex';
				});
				tokenPopupData.des = tokenPopupData.des.replace("meta_data.member_type", `
				<span style='color:green;font-weight:bold'>${memberType}
				</span>`);
				tokenPopupData.des = tokenPopupData.des.replace("{{", "");
				tokenPopupData.des = tokenPopupData.des.replace("}}", "");
				tokenPopupData.sub_desc = tokenPopupData.sub_desc.replace("meta_data.est_delivery_date", `
				<span style='font-weight:bold'>${deliveryDate}
				</span>`);
				tokenPopupData.sub_desc = tokenPopupData.sub_desc.replace("{{", "");
				tokenPopupData.sub_desc = tokenPopupData.sub_desc.replace("}}", "");
				tokenPopupData.des = replaceNewLine(tokenPopupData.des);
				tokenPopupData.sub_desc = replaceNewLine(tokenPopupData.sub_desc);
				tokenWidgetModalHeader.innerHTML = tokenPopupData.title;
				tokenWidgetModalDesc.innerHTML = tokenPopupData.des;
				tokenWidgetModalSubDesc.innerHTML = tokenPopupData.sub_desc;
				tokenWidgetModalNote.innerHTML = tokenPopupData.note;
				if (tokenPopupData.action == "issue_token") {
				tokenWidgetModalButton.addEventListener('click', createToken);
				tokenWidgetModalButtonText.innerHTML = "Confirm";
				tokenWidgetModalButtonText.style.width = "unset";
				tokenWidgetModalAddr.style.display = "block";
				}
				else {
				tokenWidgetModalButton.addEventListener('click', redirectAddAddress);
				tokenWidgetModalButtonText.innerHTML = "Add Address";
				tokenWidgetModalButtonText.style.width = "80px";
				tokenWidgetModalAddr.style.display = "none";
				}
				tokenWidgetModalCancelButton.addEventListener('click', closeModal);
				}
				else {
				tokenWidgetBtn.style.backgroundColor = "#e1e1e1";
				tokenWidgetBtn.style.cursor = "auto";
				tokenWidgetBtnText.style.color = "#a9a9a9";
				}
				}
				else {
				tokenWidget.style.display = 'none';
				}
				}).catch(error => {
				console.log("Problem in fetching token status: ", error);
				tokenWidget.style.display = 'none';
				});
				};
				backdrop.addEventListener('click', closeModal);
				var cookies = document.cookie;
				if(cookies.includes("_bb_mid")) {
				init();
				}
